package com.quraanali.assignment.model.repositoryimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport com.quraanali.assignment.model.repository.remote.RetrofitServiceimport com.quraanali.assignment.model.models.Articlesimport retrofit2.Callimport retrofit2.Callbackimport retrofit2.Responseclass RemoteRepository : RepositoryInterface {    override fun getArticles(): LiveData<Articles>? {        val data = MutableLiveData<Articles>()        val retrofitService = RetrofitService()        retrofitService.retrofitService().getArticles()            .enqueue(object : Callback<Articles> {                override fun onFailure(                    call: Call<Articles>,                    t: Throwable                ) {                    data.value = null                }                override fun onResponse(                    call: Call<Articles>,                    response: Response<Articles>                ) {                    if (response.isSuccessful) {                        data.value = response.body()                    } else {                        data.value = null                    }                }            })        return data    }}